import React from 'react';
import { useState,useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import Profile from '../profile';


function Myform() {
    const [username, setName] = useState("");
    const [data,setData]=useState([]);
       
    const root = ReactDOM.createRoot(document.getElementById('root'));
    const Shoot = () => {
       
        const getData=()=>{
          fetch('./text.json'
          ,{
            headers : { 
              'Content-Type': 'application/json',
              'Accept': 'application/json'
             }
          }
          )
         
            .then(function(myJson) {
              console.log(myJson);
              setData(myJson)
            });
        }
        useEffect(()=>{
          getData()
        },[]);
      
        root.render(<Profile Username={username} Avatar_url= {data.avatar_url}/>);
   
      }
    
     
     
    return( <div aria-label="breadcrumb" class="bg-light rounded-3 p-3 mb-4" method="post">
    <input type="text" class="from-contorl" placeholder="insert username" value={username} onChange={(e) => setName(e.target.value)}/>
    <button onClick={Shoot}>get data</button></div>);
    
  }


  export default Myform;
